<template>
    <div class="phone-container" :style="{width:width + 'px'}">
        <div class="head" :style="{height:width*0.168 +'px'}">
            <img :style="{height:width*0.168 +'px'}" src="data:image/png;base64," alt="">
            <div class="link">{{link}}</div>
            <span class="refresh" @click="refreshIframe"></span>
        </div>
        <div class="body" :style="{height:height - width*0.168 +'px'}">
            <div v-if="!iframe" v-html="content">
            </div>
            <div v-if="iframe" class="iframe-container" seamless scrolling="no">
                <iframe :src="myUrl" :style="{height:height - width*0.168 +'px'}"></iframe>
            </div>
        </div>
    </div>
</template>
<script>
export default {
    name:'phone-container',
    data(){
        return {
            myUrl:''
        }
    },
    props:{
        width:{
            default:375,
            type:Number
        },
        height:{
            default:640,
            type:Number
        },
        link:{
            default:'',
            type:String
        },
        iframe:{
            default:false,
            type:Boolean
        },
        content:{
            default:'',
            type:String
        },
        url:{
            default:'',
            type:String
        },
        reflush:{
            default:false,
            type:Boolean
        }
    },
    created () {
        this.myUrl = this.url + ''
    },
    methods: {
        refreshIframe(){
            if(!this.reflush) return
            this.myUrl = ''
            setTimeout( ()=>{
                this.myUrl = this.url + ''
            },20)
        }
    }
}
</script>
<style scoped lang="scss">
.phone-container{
    .head{
        width: 100%;
        text-align: center;
        position: relative;
        overflow: hidden;
        img{
            width: 100%;
            height: 63px;
        }   
    }
    .link{
        position: absolute;
        left: 0;
        right: 0;
        bottom: 23%;
        font-size: 14px;
    }
    .body{
        border: 1px solid #eee;
        background: #fff;
        iframe{
            width: 100%;
            height: 100%;
            border-top: 1px solid #ededed;
            border-left: none;
            border-right: none;
            border-bottom: none;
        }
    }
    .refresh{
        height: 26px;
        width: 26px;
        bottom: 20%;
        position: absolute;
        right: 3%;
        background-color:transparent;
    }
}

</style>
